import React, { useState, useEffect } from 'react';
import { Menu } from 'antd';
import { DashboardOutlined, AppstoreOutlined, FileTextOutlined, SettingOutlined, MenuOutlined } from '@ant-design/icons';
import logoImage from '../../assets/logo.png'; // Update this path according to your project structure

const SideBar = () => {
  const [isCollapsed, setIsCollapsed] = useState(false); // Sidebar is visible initially
  const [isMobile, setIsMobile] = useState(false); // Mobile detection

  const toggleSidebar = () => {
    setIsCollapsed(!isCollapsed); // Toggle sidebar state
  };

  // Listen for window resize events to determine if we are in mobile view
  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth <= 768); // Check if screen width is 768px or less
      if (window.innerWidth > 768) {
        setIsCollapsed(false); // Sidebar always visible on larger screens
      }
    };

    window.addEventListener('resize', handleResize);
    handleResize(); // Initial check on component mount

    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // Define inline styles
  const styles = {
    sidebar: {
      width: isCollapsed ? '0px' : '240px',
      backgroundColor: '#1F263E',
      height: '100vh',
      padding: '20px',
      color: 'white',
      transition: 'width 0.3s ease',
      overflow: 'hidden',
    },
    logo: {
      display: 'flex',
      alignItems: 'center',
      marginBottom: '20px',
    },
    logoImg: {
      width: '40px',
      height: '40px',
      marginRight: '10px',
    },
    logoText: {
      fontFamily: 'Inter, sans-serif',
      fontSize: '24px',
      fontWeight: 'bold',
      color: 'white',
    },
    menu: {
      color: 'white',
    },
    hamburger: {
      fontSize: '24px',
      color: 'white',
      padding: '10px',
      cursor: 'pointer',
    }
  };

  return (
    <>
      {/* Hamburger button only visible on mobile */}
      {isMobile && (
        <div style={styles.hamburger} onClick={toggleSidebar}>
          <MenuOutlined />
        </div>
      )}
      
      {/* Sidebar */}
      <div style={styles.sidebar}>
        <div style={styles.logo}>
          <img src={logoImage} alt="Logo" style={styles.logoImg} />
          <span style={styles.logoText}>Nutritioners</span>
        </div>
        <Menu mode="vertical" defaultSelectedKeys={['1']} style={styles.menu}>
          <Menu.Item key="1" icon={<DashboardOutlined />}>
            Dashboard
          </Menu.Item>
          <Menu.Item key="2" icon={<AppstoreOutlined />}>
            My Meal
          </Menu.Item>
          <Menu.Item key="3" icon={<FileTextOutlined />}>
            My Report
          </Menu.Item>
          <Menu.Item key="4" icon={<SettingOutlined />}>
            Settings
          </Menu.Item>
        </Menu>
      </div>
    </>
  );
};

export default SideBar;
