import React, { useState } from 'react';
import '../../styles/Header.css';
import { Avatar } from 'antd';
import { DownOutlined } from '@ant-design/icons';

const Header = () => {
  const [isMenuVisible, setIsMenuVisible] = useState(false);

  const handleToggle = () => {
    setIsMenuVisible(!isMenuVisible);
  };

  return (
    <div className="header">
      <div className="hamburger-container">
        <div className="hamburger" />
        <div className="header-title">Dashboard</div>
      </div>

      {/* Phần hiển thị Avatar, tên người dùng và toggle */}
      <div className="user-section">
        <Avatar
          size={40}
          src="/path/to/your/avatar-image.png" // Đường dẫn đến hình ảnh avatar của bạn
          
        />
        <span className="user-name">Moni Roy</span>
        <div className="toggle-icon" onClick={handleToggle}>
          <DownOutlined />
        </div>

        {/* Hiển thị menu khi toggle được bật */}
        {isMenuVisible && (
          <div className="dropdown-menu">
            <div className="dropdown-item">Manage Account</div>
            <div className="dropdown-item">Log out</div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Header;
