import React, { useState } from 'react';
import '../../styles/Onboard.css';

const Onboard = (props) => {
    const [step, setStep] = useState(1);

    // Handler for "Following" button
    const nextStep = () => {
        if (step < 5) {
            setStep(step + 1);
        }
    };

    // Handler for "Back" button
    const prevStep = () => {
        if (step > 1) {
            setStep(step - 1);
        }
    };

    const renderContent = () => {
        switch (step) {
            case 1:
                return (
                    <div className="popup">
                        <h2>What is your name?</h2>
                        <input type="text" placeholder="Enter your name" />
                        <button onClick={nextStep}>Following</button>  {/* Only "Following" button for Step 1 */}
                    </div>
                );
            case 2:
                return (
                    <div className="popup">
                        <h2>What is your goal?</h2>
                        <input type="radio" name="goal" value="Gain weight" /> Gain weight
                        <input type="radio" name="goal" value="Lose weight" /> Lose weight
                        <div className="button-group">
                            <button onClick={prevStep}>Back</button>  {/* Back button */}
                            <button onClick={nextStep}>Following</button>  {/* Following button */}
                        </div>
                    </div>
                );
            case 3:
                return (
                    <div className="popup">
                        <h2>What is your activity level?</h2>
                        <input type="radio" name="activity" value="Low" /> Low
                        <input type="radio" name="activity" value="Medium" /> Medium
                        <input type="radio" name="activity" value="High" /> High
                        <div className="button-group">
                            <button onClick={prevStep}>Back</button>
                            <button onClick={nextStep}>Following</button>
                        </div>
                    </div>
                );
            case 4:
                return (
                    <div className="popup">
                        <h2>Complete your info</h2>
                        <input type="text" placeholder="Age" />
                        <input type="text" placeholder="Height" />
                        <input type="text" placeholder="Weight" />
                        <div className="button-group">
                            <button onClick={prevStep}>Back</button>
                            <button onClick={nextStep}>Following</button>
                        </div>
                    </div>
                );
            case 5:
                return (
                    <div className="popup">
                        <h2>Congratulations!</h2>
                        <p>You have completed the onboarding process.</p>
                        <button onClick={props.onComplete}>Go to Dashboard</button>
                    </div>
                );
            default:
                return null;
        }
    };

    return (
        <div className="onboard-container">
            <div className="background"></div>
            {renderContent()}
        </div>
    );
};

export default Onboard;
