import React from 'react';
import { MenuOutlined } from '@ant-design/icons'; // Hamburger icon from Ant Design

const Header = ({ toggleSidebar }) => {
  return (
    <div className="header" style={styles.header}>
      {/* Dashboard Title */}
      <div style={styles.title}>
        Dashboard
      </div>
      {/* Hamburger Icon */}
      <MenuOutlined
        className="hamburger-icon"
        onClick={toggleSidebar}
        style={styles.hamburgerIcon}
      />
    </div>
  );
};

const styles = {
  header: {
    width: '100%', // Full width of screen
    height: '96px',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'space-between',
    padding: '0 20px',
    background: '#f0f0f0', // Can change according to design
  },
  title: {
    fontSize: '24px',
    fontWeight: 'bold',
  },
  hamburgerIcon: {
    fontSize: '24px',
    cursor: 'pointer',
    display: 'none', // Hidden by default, will show in mobile view
  },
};

export default Header;
