import React, { useState, useEffect } from 'react';
import '../../styles/Header.css';
import { Avatar } from 'antd';
import { UserOutlined } from '@ant-design/icons';

const Header = () => {
  const [isMobile, setIsMobile] = useState(window.innerWidth <= 768); // Kiểm tra kích thước màn hình
  const [sidebarOpen, setSidebarOpen] = useState(!isMobile); // Đặt trạng thái sidebar

  // Kiểm tra kích thước cửa sổ khi thay đổi kích thước màn hình
  useEffect(() => {
    const handleResize = () => {
      const mobileView = window.innerWidth <= 768;
      setIsMobile(mobileView);
      setSidebarOpen(!mobileView); // Tự động đóng sidebar khi ở chế độ di động
    };
    
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen); // Toggle trạng thái sidebar
  };

  return (
    <>
      <div className={`sidebar ${sidebarOpen ? '' : 'collapsed'}`}>
        <div className="logo">Nutritioners</div>
        <div className="menu">
          <ul>
            <li className="ant-menu-item">Dashboard</li>
            <li className="ant-menu-item">My meal</li>
            <li className="ant-menu-item">Settings</li>
          </ul>
        </div>
      </div>

      <div className="header">
        <div className="hamburger-container">
          {isMobile && (
            <div className="hamburger" onClick={toggleSidebar}>
              &#9776; {/* Hamburger Icon */}
            </div>
          )}
          <div className="header-title">Dashboard</div>
        </div>
        <Avatar size="large" icon={<UserOutlined />} />
      </div>
    </>
  );
};

export default Header;
