import React, { useState, useEffect } from 'react';
import '../../styles/Sidebar.css'; // Thay bằng đường dẫn CSS của bạn

const Sidebar = () => {
  const [isCollapsed, setIsCollapsed] = useState(false); // Ban đầu sidebar sẽ không bị collapsed
  const [isMobile, setIsMobile] = useState(false); // Theo dõi nếu màn hình nhỏ hơn 768px

  const toggleSidebar = () => {
    setIsCollapsed(!isCollapsed); // Toggle trạng thái của sidebar
  };

  // Lắng nghe sự thay đổi của kích thước màn hình
  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth <= 768); // Xác định khi nào màn hình nhỏ hơn 768px
      if (window.innerWidth > 768) {
        setIsCollapsed(false); // Nếu màn hình lớn hơn, sidebar luôn hiển thị
      }
    };

    // Gọi hàm khi lần đầu component render và khi thay đổi kích thước
    window.addEventListener('resize', handleResize);

    // Gọi hàm ngay khi load để đảm bảo đúng trạng thái ban đầu
    handleResize();

    // Clean up event listener khi component unmount
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  return (
    <>
      {/* Sidebar */}
      <div className={`sidebar ${isMobile && isCollapsed ? 'collapsed' : ''}`}>
        <div className="logo">Logo</div>
        <div className="menu">
          <div className="menu-item">Dashboard</div>
          <div className="menu-item">Profile</div>
          <div className="menu-item">Settings</div>
        </div>
      </div>

      {/* Hamburger button */}
      {isMobile && (
        <div className="hamburger" onClick={toggleSidebar}>
          &#9776; {/* Icon hamburger */}
        </div>
      )}
    </>
  );
};

export default Sidebar;
