// Dashboard.js
import React from 'react';
import { Layout, Menu, Button, Table, Avatar, DatePicker } from 'antd';
import { UserOutlined, CalendarOutlined, LogoutOutlined } from '@ant-design/icons';
import '../../styles/Dashboard.css';

const { Header, Content, Sider } = Layout;

const Dashboard = () => {
  // Sample data for calorie summary (you can modify as needed)
  const summaryData = [
    {
      key: '1',
      macro: 'Protein',
      consumed: 345,
      target: 453,
      progress: '75%',
    },
    {
      key: '2',
      macro: 'Carbs',
      consumed: 245,
      target: 453,
      progress: '54%',
    },
    {
      key: '3',
      macro: 'Fat',
      consumed: 150,
      target: 200,
      progress: '75%',
    },
  ];

  const columns = [
    { title: 'Macro', dataIndex: 'macro', key: 'macro' },
    { title: 'Consumed (g)', dataIndex: 'consumed', key: 'consumed' },
    { title: 'Target (g)', dataIndex: 'target', key: 'target' },
    { title: 'Progress', dataIndex: 'progress', key: 'progress' },
  ];

  return (
    <Layout style={{ minHeight: '100vh' }}>
      <Sider width={200} className="site-layout-background">
        <div className="logo">logo</div>
        <Menu mode="vertical" defaultSelectedKeys={['1']}>
          <Menu.Item key="1">Dashboard</Menu.Item>
          <Menu.Item key="2">My meal</Menu.Item>
          <Menu.Item key="3">My report</Menu.Item>
          <Menu.Item key="4" icon={<LogoutOutlined />}>Logout</Menu.Item>
        </Menu>
      </Sider>

      <Layout>
        <Header className="header">
          <div className="user-info">
            <Avatar icon={<UserOutlined />} /> Moni Roy
          </div>
        </Header>

        <Content style={{ margin: '16px 24px' }}>
          <div className="dashboard-section">
            <h1>Calorie Summary</h1>
            <Table columns={columns} dataSource={summaryData} pagination={false} />

            <Button type="primary" className="add-food-btn">+ Add new target</Button>
          </div>
        </Content>
      </Layout>
    </Layout>
  );
};

export default Dashboard;
