import React from 'react';
import { Menu } from 'antd';

const Sidebar = ({ isVisible }) => {
  return (
    <div style={{ ...styles.sidebar, left: isVisible ? '0' : '-240px' }}>
      <Menu mode="vertical" theme="light" style={styles.menu}>
        <Menu.Item key="1">MyMeal</Menu.Item>
        <Menu.Item key="2">Dashboard</Menu.Item>
        <Menu.Item key="3">Settings</Menu.Item>
        <Menu.Item key="4">MyReport</Menu.Item>
      </Menu>
    </div>
  );
};

const styles = {
  sidebar: {
    width: '240px',
    height: '100%',
    position: 'fixed',
    top: '0',
    left: '-240px', // Hidden by default
    transition: 'left 0.3s ease',
    background: '#fff',
  },
  menu: {
    borderRight: 'none', // Remove border
  },
};

export default Sidebar;
